mapper.select:
SELECT MEMBER_KEY, MEMBER_NAME, EMAIL, PASSWORD, PHONE, PAS_WRONG, ROLE
  FROM MEMBER
 WHERE EMAIL = #{email}
   AND PASSWORD = #{password}
   AND DEL_FLAG = 0

mapper.update:
UPDATE MEMBER
   SET EMAIL = #{email},
       PASSWORD = #{password},
       PHONE = #{phone}
 WHERE MEMBER_KEY = #{memberKey}

UPDATE MEMBER
   SET DEL_FLAG = 1
 WHERE MEMBER_KEY = #{memberKey}

mapper.select:
SELECT count(*)
  FROM MEMBER
 WHERE EMAIL = #{email}
